func TestRemoveInvalidChar(t *testing.T) {

	t.Logf("len map:%v", len(invalidCharMap))

	inputs := strings.Split(content, "\n")

	for _, input := range inputs {
		//t.Logf("input:%v", input)
		output, ok := ReplaceInvalidChar(input, "")
		t.Logf("output:%v ok:%v", output, ok)
	}

}
